jQuery(function() {
	jQuery.ajax({
		type: "get",
		url: "http://zongheng.xiu8.com/xiuws/zongheng/recommendBroad?size=5",
		dataType: "jsonp",
		success: function(data) {
			if(data == null || data.length < 1) { return; }
			var a = [];
			a[a.length] = "<h3>秀场直播</h3>";
			for(var i = 0; i < data.length; i++) {
				var e = data[i];
				e.nickName = unescape(e.nickName.replace(/\\/g, "%"))
				var url = "http://passport.zongheng.com/api/coop/recocoop.do?partner=xiu8&backurl=" + encodeURIComponent("http://spread.xiu8.com/spreed!baiduGo.jspa?pid=1073&sid=1&t=2&roomId=" + e.broadId);
				a[a.length] = "<div class=\"online_urser\">";
				a[a.length] = "    <div class=\"online_pic\"><a href=\"" + url + "\" title=\"" + StringUtil.html(e.nickName) + "\" target=\"_blank\"><img alt=\"" + e.username + "\" src=\"http://image.cache.xiu8.com/live/125/90/" + e.broadId + ".jpg\"/>";
				a[a.length] = "        <em class=\"play\" style=\"display: none;\"></em></a>";
				a[a.length] = "    </div>";
				a[a.length] = "    <div class=\"online_time\">";
				a[a.length] = "        <p align=\"center\"><a href=\"" + url + "\" title=\"" + StringUtil.html(e.nickName) + "\" target=\"_blank\">" + e.nickName + "</a></p>";
				a[a.length] = "    </div>";
				a[a.length] = "</div>";
			}
			a[a.length] = "<div class=\"cl0\"></div>";
			jQuery("#6roomRecommend").html(a.join(""));
			jQuery("#6roomRecommend div.online_urser").hover(function() { jQuery(this).find("em.play").css("display", "block"); }, function() { jQuery(this).find("em.play").css("display", "none"); });
		}
	});
});
jQuery(function() {
	if(jQuery("#monthTicketRankList").size() < 1) { return false; }
	var rankType = jQuery("#monthTicketRankList").attr("rankType");
	if(rankType == null) { rankType = "1"; }
	jQuery.ajax({
		type: "get",
		url: Domain.bookHostName + "/api/rank/getZongHengMonthTicketRankList.htm?rankType=" + rankType + "&pageNum=1&pageSize=10",
		dataType: "jsonp",
		async: true,
		error: function() {},
		success: function(data) {
			if(data == null || data.length == null) { return; }
			var buffer = [];
			for(var i = 0, length = data.length; i < length; i++) {
				var e = data[i];
				if(i < length - 1) { buffer[buffer.length] = "<li><span>" + e.number + "</span>"; } else { buffer[buffer.length] = "<li class=\"last\"><span>" + e.number + "</span>"; }
				if(i < 3) { buffer[buffer.length] = "<em class=\"list_icon top jrsj_plus\" bookId=\"" + e.bookId + "\" order=\"" + (i + 1) + "\" title=\"加入书架\">" + (i + 1) + "</em>"; } else { buffer[buffer.length] = "<em class=\"list_icon jrsj_plus\" bookId=\"" + e.bookId + "\" order=\"" + (i + 1) + "\" title=\"加入书架\">" + (i + 1) + "</em>"; }
				buffer[buffer.length] = "<!-- a href=\"" + Domain.bookHostName + "/category/" + e.bookCatePid + ".html\" class=\"book_style\">" + e.bookShortCateName + "</a --><a href=\"" + Domain.bookHostName + "/book/" + e.bookId + ".html\" title=\"" + StringUtil.html(e.bookName) + "\" target=\"_blank\">" + StringUtil.substring(e.bookName, 20, "...") + "</a></li>";
			}
			jQuery("#monthTicketRankList").html(buffer.join(""));
			BookmarkSupport.bind(jQuery("#monthTicketRankList"));
		}
	});
});
jQuery(function() {
	jQuery(".tab_block h2").mouseover(function() {
		jQuery(this).closest(".tab_block").siblings().children("div.detail").css("display", "none");
		jQuery(this).closest(".tab_block").siblings().children("h2.item").css("display", "block");
		if(jQuery(this).attr("class").indexOf("item") > -1) { jQuery(this).css("display", "none"); }
		jQuery(this).siblings("div.detail").css("display", "block");
	});
});